INSERT INTO `b_uts_iblock_12_section` (`VALUE_ID`, `UF_INFOTEXT`, `UF_VIEWTYPE`) VALUES
(16, 'Система сочетает в себе отличные показатели по теплоизоляции, высокое качество профиля и элегантный дизайн каждого изделия. ', NULL),
(17, 'Автоматические регуляторы напряжения обеспечивают защиту электронного оборудования от повышенного и пониженного напряжения в электросети. ', 5),
(18, 'Сочетает в себе современный дизайн, простоту и безупречный эстетику благодаря благородным материалам. ', NULL),
(19, 'Широкий выбор мультимедиа проекторы. У нас быстрая доставка в любое удобное для вас время.', 5),
(20, 'Лазерное цветное многофункциональное устройство может быть подключено к офисной сети Ethernet. ', NULL),
(21, 'Для приборов характерен ряд параметров: диапазон измерений на который рассчитан прибор.', NULL);
INSERT INTO `b_uts_iblock_13_section` (`VALUE_ID`, `UF_INFOTEXT`) VALUES
(22, 'Специальные условия для юридических лиц и индивидуальных предпринимателей по проведению налогового аудита.'),
(23, 'Новая услуга по поддержке корпоративных клиентов и предоставлению услуг ИТ-аутсорсинга на выгодных условиях.'),
(24, 'Линейка тарифов по обслуживанию инфраструктуры вашего предприятия с целью защиты информации и систем.'),
(25, 'Создание проекта предполагаемого или возможного объекта, комплекта документации, предназначенной для создания определённого объекта.');
INSERT INTO `b_uts_iblock_14_section` (`VALUE_ID`, `UF_INFOTEXT`) VALUES
(26, 'Корпоративное обучение важно как для роста каждого отдельно взятого сотрудника так и для компании в целом, ведь как говорил И.В. Сталин - Кадры решают все!'),
(27, 'Cтанки отличается малыми габаритами, что позволяет разместить их в помещениях с ограниченным пространством. Агрегаты предназначены для работы с деревянными заготовками.'),
(28, 'Особенности обучения английскому языку взрослых людей.'),
(29, 'Детей лучше всего начинать обучать английскому языку в возрасте от 5 лет, так вы сможете подготовить их к более углубленному изучению языка в более зрелом возрасте.');
